const nodeId = msg.payload.nodeId;
const contact = contacts[nodeId];

if (contact) {
    msg.contact = contact;
    msg.payload.contact = contact;
    return msg;
} else {
    msg.contact = {
        name: "Unknown Node",
        email: null,
        owner: "Unknown",
        pins: {},
        analog: {}
    };
    return msg;
}

// Combined digital and analog alert detection
const digitalValue = msg.payload.digital;
const analogValues = msg.payload.analog; // Array of analog readings
const nodeId = msg.payload.nodeId;
const contact = msg.contact;
const alerts = [];

if (!contact) {
    return [msg, null];
}

// Digital pin alerts (existing code)
if (contact.pins) {
    for (let pinNum = 0; pinNum < 8; pinNum++) {
        const pinConfig = contact.pins[pinNum];
        if (!pinConfig) continue;

        const pinState = (digitalValue & (1 << pinNum)) !== 0;
        const shouldAlert = (pinConfig.triggerOn === "HIGH" && pinState) || 
                           (pinConfig.triggerOn === "LOW" && !pinState);

        if (shouldAlert) {
            alerts.push(createDigitalAlert(pinConfig, pinNum, pinState, contact, msg.payload));
        }
    }
}

// Analog pin alerts (NEW)
if (contact.analog && analogValues) {
    for (let analogPin = 0; analogPin < analogValues.length; analogPin++) {
        const analogConfig = contact.analog[analogPin];
        if (!analogConfig) continue;

        const currentValue = analogValues[analogPin];

        // Check low threshold
        if (analogConfig.lowThreshold !== null && currentValue < analogConfig.lowThreshold) {
            alerts.push(createAnalogAlert(analogConfig, analogPin, currentValue, "LOW", contact, msg.payload));
        }

        // Check high threshold
        if (analogConfig.highThreshold !== null && currentValue > analogConfig.highThreshold) {
            alerts.push(createAnalogAlert(analogConfig, analogPin, currentValue, "HIGH", contact, msg.payload));
        }
    }
}

// Helper functions
function createDigitalAlert(pinConfig, pinNum, pinState, contact, payload) {
    return {
        payload: {
            alert: true,
            alertType: "DIGITAL",
            type: pinConfig.type.toUpperCase() + "_ALERT",
            severity: pinConfig.alertLevel,
            nodeId: contact.nodeId || payload.nodeId,
            contact: contact,
            pin: pinNum,
            pinLabel: pinConfig.label,
            pinState: pinState ? "HIGH" : "LOW",
            triggerCondition: pinConfig.triggerOn,
            timestamp: payload.time,
            message: `${getAlertIcon(pinConfig.alertLevel)} ${pinConfig.label}: ${contact.name} (Pin ${pinNum} is ${pinState ? "HIGH" : "LOW"})`,
            digitalValue: payload.digital
        },
        topic: `alert/digital/${pinConfig.type}`
    };
}

function createAnalogAlert(analogConfig, analogPin, currentValue, thresholdType, contact, payload) {
    const threshold = thresholdType === "LOW" ? analogConfig.lowThreshold : analogConfig.highThreshold;
    const condition = thresholdType === "LOW" ? "below" : "above";

    return {
        payload: {
            alert: true,
            alertType: "ANALOG",
            type: analogConfig.type.toUpperCase() + "_ALERT",
            severity: analogConfig.alertLevel,
            nodeId: contact.nodeId || payload.nodeId,
            contact: contact,
            analogPin: analogPin,
            analogLabel: analogConfig.label,
            currentValue: currentValue,
            threshold: threshold,
            thresholdType: thresholdType,
            unit: analogConfig.unit,
            timestamp: payload.time,
            message: `${getAlertIcon(analogConfig.alertLevel)} ${analogConfig.label}: ${contact.name} (${currentValue}${analogConfig.unit} is ${condition} ${threshold}${analogConfig.unit})`,
            analogValues: payload.analog
        },
        topic: `alert/analog/${analogConfig.type}`
    };
}

