        case "LOW": return "â„¹ï¸";
        default: return "ðŸ“";
    }
}

// Set flags for specific alert types
alerts.forEach(alert => {
    if (alert.payload.type === "BILGE_PUMP_ALERT") {
        msg.payload.bilgePumpAlert = true;
    }
    if (alert.payload.type === "FUEL_LEVEL_ALERT") {
        msg.payload.fuelAlert = true;
    }
    if (alert.payload.type === "ENGINE_TEMP_ALERT") {
        msg.payload.engineTempAlert = true;
    }
});

// Return main message + all alerts
return [msg].concat(alerts);
