switch (topic) {
    case "get_owners":
        // Fetch the list of owners from the global context
        const owners = global.get("owners") || {};
        msg.payload = owners; // Set the payload to owners
        msg.topic = "owners_data"; // Change the topic for the response
        node.log("Fetched owners: " + JSON.stringify(owners)); // Log the owners
        return msg; // Ensure to return the message

    case "add_owner":
        // Add a new owner
        const allOwners = global.get("owners") || {};

        // Ensure the owner name is provided and is unique
        if (!payload.name) {
            node.log("Owner name is missing. Cannot add owner."); // Log missing name error
            return { topic: "owner_error", payload: { error: "Owner name is required." } }; // Return error feedback
        }

        // Check for unique owner name
        if (allOwners[payload.name]) {
            node.log("Owner name already exists: " + payload.name); // Log duplicate name error
            return { topic: "owner_error", payload: { error: "Owner name must be unique." } }; // Return error feedback
        }

        // Create new owner object with name and other details
        allOwners[payload.name] = {
            name: payload.name, // Include the name property
            email: payload.email || "",
            phone: payload.phone || "",
            devices: [], // Initialize associated devices as an empty array
            created: new Date().toISOString() // Set the creation timestamp
        };

        global.set("owners", allOwners); // Save updated owners to global context
        node.log("Added owner : " + payload.name); // Log the newly added owner'        
        node.log("Global should be set to : " + allOwners); // Log the newly added owner'
        node.log("Global now has : " + global.get("owners")); // Log the newly added owner

        msg.payload = allOwners; // Set the payload to the updated list of owners
        msg.topic = "owners_data"; // Change the topic for the response
        return msg; // Ensure to return the message

    case "update_owner":
        // Update an existing owner
        const ownersToUpdate = global.get("owners") || {};
        node.log("Incoming payload for update: " + JSON.stringify(payload)); // Log incoming payload
        if (ownersToUpdate[payload.name]) {
            node.log("Current owners before update: " + JSON.stringify(ownersToUpdate)); // Log owners state

            // Retain existing properties and update with new data
            ownersToUpdate[payload.name] = {
                ...ownersToUpdate[payload.name],
                ...payload,  // Change from payload.data to payload to ensure all fields are merged correctly
                updated: new Date().toISOString() // Update the timestamp
            };
            global.set("owners", ownersToUpdate); // Save the updated owner info

            node.log("Updated owner: " + JSON.stringify(ownersToUpdate[payload.name])); // Log the update
            msg.payload = ownersToUpdate[payload.name]; // Send just the updated owner info
            msg.topic = "owner_updated"; // Change to specific topic for the update event
        } else {
            node.log("Owner name not found: " + payload.name); // Log if the owner name does not exist
            return { topic: "owner_error", payload: { error: "Owner name not found." } }; // Return error feedback
        }
        return msg; // Return message for success

    case "delete_owner":
        // Delete an owner and their associated devices
        const ownersToDelete = global.get("owners") || {};
        const devices = global.get("devices") || {}; // Retrieve existing devices

        node.log("Trying to delete owner: " + payload.name); // Log the owner to delete
        node.log("Current owners: " + JSON.stringify(ownersToDelete)); // Log current owners for debugging

        if (ownersToDelete[payload.name]) {
            // Get the IDs of devices associated with this owner
            const ownerDevices = ownersToDelete[payload.name].devices || [];

            // Remove any associated devices from the devices registry
            ownerDevices.forEach(deviceName => {
                delete devices[deviceName]; // Remove the device from the global devices object
            });

            // Update the global devices list
            global.set("devices", devices);

            // Now delete the owner
            delete ownersToDelete[payload.name];
            global.set("owners", ownersToDelete); // Save updated owners
            node.log("Deleted owner: " + payload.name + " and their devices"); // Log the deletion
        } else {
            node.log("Owner name not found for deletion: " + payload.name); // Log if the owner name does not exist
            return { topic: "owner_error", payload: { error: "Owner name not found for deletion." } }; // Return error feedback
        }

        msg.payload = ownersToDelete; // Set payload to the updated owner list
        msg.topic = "owners_data"; // Change the topic for the response
        return msg; // Return message for success

    default:
        // Log if the topic is unrecognized
        return null;
}

