        msg.payload = devicesData;
        msg.topic = "devices_data";
        node.log("Fetched devices: " + JSON.stringify(devicesData));
        return msg;

    case "add_device":
        const allDevices = global.get("devices") || {};
        const allOwners = global.get("owners") || {};

        // Ensure required fields are present
        if (!payload.nodeId || !payload.displayName || !payload.ownerName) {
            node.log("Node ID, Display Name, or Owner Name is missing.");
            return { topic: "device_error", payload: { error: "Node ID, Display Name, and Owner Name are required." } };
        }

        // Ensure node ID is unique (this is the immutable identifier)
        if (!allDevices[payload.nodeId]) {
            // Initialize pin structures with proper defaults
            const defaultBinaryPins = Array(8).fill(null).map((_, index) => ({
                label: payload.binaryPins && payload.binaryPins[index] ? payload.binaryPins[index].label || "" : "",
                trigger: payload.binaryPins && payload.binaryPins[index] ? payload.binaryPins[index].trigger || "High" : "High",
                alertLevel: payload.binaryPins && payload.binaryPins[index] ? payload.binaryPins[index].alertLevel || "None" : "None"
            }));

            const defaultAnalogPins = Array(4).fill(null).map((_, index) => ({
                value: payload.analogPins && payload.analogPins[index] ? payload.analogPins[index].value || "" : "",
                lowThreshold: payload.analogPins && payload.analogPins[index] ? payload.analogPins[index].lowThreshold || 0 : 0,
                highThreshold: payload.analogPins && payload.analogPins[index] ? payload.analogPins[index].highThreshold || 100 : 100,
                alertLevel: payload.analogPins && payload.analogPins[index] ? payload.analogPins[index].alertLevel || "None" : "None"
            }));

            allDevices[payload.nodeId] = {
                nodeId: payload.nodeId,           // Immutable unique identifier
                displayName: payload.displayName, // User-friendly name (can be changed)
                ownerName: payload.ownerName,
                binaryPins: defaultBinaryPins,
                analogPins: defaultAnalogPins,
                lastSeen: null
            };

            // Add device to the owner's device list using nodeId
            if (allOwners[payload.ownerName]) {
                if (!allOwners[payload.ownerName].devices.includes(payload.nodeId)) {
                    allOwners[payload.ownerName].devices.push(payload.nodeId);
                } else {
                    node.log("Device already exists in owner's device list: " + payload.nodeId);
                    return { topic: "device_error", payload: { error: "Device already exists for this owner." } };
                }
            } else {
                node.log("Owner not found, cannot assign device: " + payload.ownerName);
                return { topic: "device_error", payload: { error: "Owner not found." } };
            }

            global.set("devices", allDevices);
            global.set("owners", allOwners);

            node.log("Added device: " + JSON.stringify(allDevices[payload.nodeId]));

            msg.topic = "device_added";
            msg.payload = allDevices[payload.nodeId];
        } else {
            node.log("Node ID already exists: " + payload.nodeId);
            return { topic: "device_error", payload: { error: "Node ID already exists." } };
        }
        break;

    case "update_device":
        const devicesToUpdate = global.get("devices") || {};
        const ownersToUpdate = global.get("owners") || {};

        const currentNodeId = payload.nodeId; // Use nodeId as the immutable key
        const currentDevice = devicesToUpdate[currentNodeId];

        if (!currentDevice) {
            node.log("Node ID not found for update: " + currentNodeId);
            return { topic: "device_error", payload: { error: "Node ID not found." } };
        }

        node.log("Current Device before update: " + JSON.stringify(currentDevice));

        // Update display name (this can change)
        if (payload.displayName) {
            currentDevice.displayName = payload.displayName;
            node.log("Updated display name to: " + payload.displayName);
        }

        // Update the device's owner name if it has changed
        if (payload.ownerName && payload.ownerName !== currentDevice.ownerName) {
            const oldOwnerName = currentDevice.ownerName;
            currentDevice.ownerName = payload.ownerName;

            node.log("Updated device owner from " + oldOwnerName + " to " + currentDevice.ownerName);

            // Remove device from old owner's device list using nodeId
            if (oldOwnerName && ownersToUpdate[oldOwnerName]) {
                const oldOwnerDevices = ownersToUpdate[oldOwnerName].devices;
                const index = oldOwnerDevices.indexOf(currentNodeId);
                if (index > -1) {
                    oldOwnerDevices.splice(index, 1);
                    node.log("Removed device from old owner: " + oldOwnerName);
                }
            }

            // Add device to the new owner's list using nodeId
            if (ownersToUpdate[currentDevice.ownerName]) {
                if (!ownersToUpdate[currentDevice.ownerName].devices.includes(currentNodeId)) {
                    ownersToUpdate[currentDevice.ownerName].devices.push(currentNodeId);
                    node.log("Added device to new owner: " + currentDevice.ownerName);
                }
            }
        }

        // Update pin configurations if provided
        if (payload.binaryPins) {
            currentDevice.binaryPins = payload.binaryPins;
        }
        if (payload.analogPins) {
            currentDevice.analogPins = payload.analogPins;
        }

        global.set("devices", devicesToUpdate);
        global.set("owners", ownersToUpdate);

        node.log("Updated devices structure after update: " + JSON.stringify(devicesToUpdate));

        msg.payload = devicesToUpdate;
        msg.topic = "devices_data";
        return msg;

    case "delete_device":
        const remainingDevices = global.get("devices") || {};
        const remainingOwners = global.get("owners") || {};

        const deviceToDeleteNodeId = payload.nodeId; // Use nodeId for deletion
        const deviceToDelete = remainingDevices[deviceToDeleteNodeId];

        if (!deviceToDelete) {
            node.log("Node ID not found for deletion: " + deviceToDeleteNodeId);
            return { topic: "device_error", payload: { error: "Node ID not found." } };
        }

        // Remove device from owner's device list using nodeId
        const deviceOwnerName = deviceToDelete.ownerName;
        if (deviceOwnerName && remainingOwners[deviceOwnerName]) {
            const ownerDevices = remainingOwners[deviceOwnerName].devices;
            const index = ownerDevices.indexOf(deviceToDeleteNodeId);
            if (index > -1) {
                ownerDevices.splice(index, 1);
                node.log("Removed device from owner's list: " + deviceOwnerName);
            }
        }

        delete remainingDevices[deviceToDeleteNodeId];

        global.set("devices", remainingDevices);
        global.set("owners", remainingOwners);

        node.log("Deleted device: " + deviceToDeleteNodeId);

        msg.payload = { message: "Device deleted successfully.", nodeId: deviceToDeleteNodeId };
        msg.topic = "device_deleted";
        return msg;

    default:
        return null;
}

return msg;

const topic = msg.topic; // Retrieve the topic from the incoming message
const payload = msg.payload; // Retrieve the payload containing the necessary data

// Retrieve the devices from global context
const devices = global.get("devices") || {};
const currentDeviceName = payload.nodeId.toString(); // Use nodeId for identification
const currentDevice = devices[currentDeviceName];

node.log("Received message for topic: " + topic + " json: " + JSON.stringify(msg)); // Log the whole message

if (!currentDevice) {
    node.warn("Device not found for nodeId: " + payload.nodeId);
    return null; // Stop processing if the device is not found
}

// Initialize an array to collect alerts
const alerts = [];

// Check binary pins using bitwise operations
const binaryPinStates = payload.digital; // Assuming msg.payload.digital contains the binary pin byte
node.log("Binary pin states received: " + binaryPinStates.toString(2).padStart(currentDevice.binaryPins.length, '0')); // Log binary states in binary format

currentDevice.binaryPins.forEach((pin, index) => {
    // Check if pin label is defined; if not, skip this pin
    if (!pin.label) {
        node.log(`Skipping binary pin index: ${index} because pin label is undefined.`);
        return; // Skip to the next iteration
    }

    // Get the state of the current binary pin using bitwise AND and shifting
    const pinState = (binaryPinStates >> index) & 1; // Get the value of the current bit
    node.log(`Checking binary pin index: ${index}, pin label: ${pin.label}, pin state: ${pinState}`); // Log the current pin evaluation

    // Check for High trigger
    if (pin.trigger === 'High' && pinState === 1) { // High trigger alert
        alerts.push({
            ownerName: currentDevice.ownerName,
            nodeId: payload.nodeId, //  Added nodeId
            pinLabel: pin.label,
            alertMessage: `Triggered HIGH`,
            timestamp: payload.time_iso
        });
        node.log(`Alert added for binary pin: ${pin.label} HIGH`); // Log alert addition
    }
    // Check for Low trigger
    else if (pin.trigger === 'Low' && pinState === 0) { // Low trigger alert
        alerts.push({
            ownerName: currentDevice.ownerName,
            nodeId: payload.nodeId, //  Added nodeId
            pinLabel: pin.label,
            alertMessage: `Triggered LOW`,
            timestamp: payload.time_iso
        });
        node.log(`Alert added for binary pin: ${pin.label} LOW`); // Log alert addition
    } else {
        node.log(`No alert triggered for binary pin ${pin.label}. Expected: ${pin.trigger}, Actual state: ${pinState}`); // Log when no alert is triggered
    }
});

// Check analog pins
currentDevice.analogPins.forEach((pin, index) => {
    // Check if pin label is defined; if not, skip this pin
    if (!pin.label) {
        node.log(`Skipping analog pin index: ${index} because pin label is undefined.`);
        return; // Skip to the next iteration
    }

    // Assuming msg.payload.analog holds the pin values for the corresponding index
    if (payload.analog && msg.payload.analog[index] !== undefined) {
        const analogValue = msg.payload.analog[index];
        node.log(`Checking analog pin index: ${index}, pin label: ${pin.label}, value: ${analogValue}`); // Log the current analog evaluation
        if (analogValue < pin.lowThreshold) { // Low threshold alert
            alerts.push({
                ownerName: currentDevice.ownerName,
                nodeId: payload.nodeId, //  Added nodeId
                pinLabel: pin.label,
                alertMessage: `Analog value is LOW: ${analogValue}`,
                timestamp: payload.time_iso
            });
            node.log(`Alert added for analog pin: ${pin.label} LOW threshold`); // Log alert for low threshold
        } else if (analogValue > pin.highThreshold) { // High threshold alert
            alerts.push({
                ownerName: currentDevice.ownerName,
                nodeId: payload.nodeId, //  Added nodeId
                pinLabel: pin.label,
                alertMessage: `Analog value is HIGH: ${analogValue}`,
                timestamp: payload.time_iso
            });
            node.log(`Alert added for analog pin: ${pin.label} HIGH threshold`); // Log alert for high threshold
        } else {
            node.log(`No alert for analog pin ${pin.label}. Value within range: ${analogValue}`); // Log if value is within range
        }
    } else {
        node.log(`Analog pin index ${index} does not exist in payload. Skipping.`); // Log if analog data is missing
    }
});

// Send the structured alerts to the topic "add_alert"
if (alerts.length > 0) {
    alerts.forEach(alert => {
        node.send({
            topic: "add_alert",
            payload: alert
        });
    });
    node.log(`Total alerts sent: ${alerts.length}`); // Log total number of alerts sent
} else {
    node.log("No alerts to send."); // Log when no alerts are present
}

