    // Create composite key using nodeId to prevent duplicates from same device/pin
    const compositeKey = `${payload.nodeId}_${payload.pinLabel}`;

    // Check if alert already exists for this device/pin combo
    if (allAlerts[compositeKey]) {
        // Update existing alert instead of creating duplicate
        allAlerts[compositeKey] = {
            ...allAlerts[compositeKey],
