            alertId: compositeKey,
            alertMessage: payload.alertMessage,
            ownerName: payload.ownerName,
            deviceName: deviceName,
            nodeId: payload.nodeId,
            pinLabel: payload.pinLabel,
            timestamp: new Date().toISOString(),
            count: 1
        };
