    }

    global.set("alerts", allAlerts);
    msg.payload = Object.values(allAlerts);
    msg.topic = "alerts_data";
    return msg;

    case "clear_alert":
        const alertsToClear = global.get("alerts") || {};
        if (!payload.alertId) {
            node.log("Alert ID is missing. Cannot clear alert.");
            return { topic: "alert_error", payload: { error: "Alert ID is required." } };
        }

        if (alertsToClear[payload.alertId]) {
            delete alertsToClear[payload.alertId];
            global.set("alerts", alertsToClear);
            node.log("Cleared alert: " + payload.alertId);
            msg.payload = Object.values(alertsToClear);
            msg.topic = "alerts_data";
        } else {
            node.log("Alert ID not found: " + payload.alertId);
            return { topic: "alert_error", payload: { error: "Alert ID not found." } };
        }
        return msg;

    case "update_alert":
        const alertsToUpdate = global.get("alerts") || {};
        node.log("Incoming payload for update: " + JSON.stringify(payload));
        if (alertsToUpdate[payload.alertId]) {
            alertsToUpdate[payload.alertId] = {
                ...alertsToUpdate[payload.alertId],
                ...payload,
                updated: new Date().toISOString()
            };
            global.set("alerts", alertsToUpdate);
            node.log("Updated alert: " + JSON.stringify(alertsToUpdate[payload.alertId]));
            msg.payload = Object.values(alertsToUpdate);
            msg.topic = "alerts_data";
        } else {
            node.log("Alert ID not found: " + payload.alertId);
            return { topic: "alert_error", payload: { error: "Alert ID not found." } };
        }
        return msg;

    case "delete_alert":
        const alertsToDelete = global.get("alerts") || {};
        if (!payload.alertId) {
            node.log("Alert ID is missing. Cannot delete alert.");
            return { topic: "alert_error", payload: { error: "Alert ID is required." } };
        }

        if (alertsToDelete[payload.alertId]) {
            delete alertsToDelete[payload.alertId];
            global.set("alerts", alertsToDelete);
            node.log("Deleted alert: " + payload.alertId);
            msg.payload = Object.values(alertsToDelete);
            msg.topic = "alerts_data";
        } else {
            node.log("Alert ID not found.");
            return { topic: "alert_error", payload: { error: "Alert ID not found." } };
        }
        return msg;

    case "clear_all_alerts":
        const ownerNameToClear = payload.ownerName;
        const alertsToClearAll = global.get("alerts") || {};

        // Log the current state of alerts before clearing
        node.log("Current alerts before clearing for owner " + ownerNameToClear + ": " + JSON.stringify(alertsToClearAll));

        // Create an object to store alerts that will remain after clearing
        const remainingAlerts = {};

        // Iterate through all alerts and remove alerts for the specified owner
        for (const alertId in alertsToClearAll) {
            if (alertsToClearAll[alertId].ownerName !== ownerNameToClear) {
                remainingAlerts[alertId] = alertsToClearAll[alertId]; // Preserve this alert
            } else {
                node.log(`Clearing alert: ${alertId} for owner: ${ownerNameToClear}`);
            }
        }

        // Log the alerts that will be cleared and those that will remain
        node.log("Alerts to be cleared: " + JSON.stringify(Object.keys(alertsToClearAll).filter(alertId => alertsToClearAll[alertId].ownerName === ownerNameToClear)));
        node.log("Remaining alerts after clearing: " + JSON.stringify(remainingAlerts));

        // Update global alerts to only include remaining alerts
        global.set("alerts", remainingAlerts);

        // Return the updated alerts list back to the frontend
        msg.payload = Object.values(remainingAlerts); // Convert remaining alerts to an array
        msg.topic = "alerts_data"; // Change topic for the response
        return msg;

